     /* Le getter de l'attribut title */
     public function getTitle() : string 
     {

        return $this->title;
     }
 
     /* Le setter de l'attribut title */
     public function setTitle(string $title) : void
     {
        $this->validateTitle($title);
        $this->title = $title;
     }

    /* Le getter de l'attribut content */
    public function getContent() : string 
    {
        return $this->content;
    }

    /* Le setter de l'attribut content */
    public function setContent(string $content) : void
    {
        $this->validateContent($content);
        $this->content = $content;
    }

    /* Le getter de l'attribut slug */
    public function getSlug() : string 
    {
        return $this->slug;
    }

    /* Le setter de l'attribut slug */
    public function setSlug(string $slug) : void
    {
        $this->validateSlug($slug);
        $this->author = $slug;
    }

    /* Le getter de l'attribut private */
    public function getPrivate() : bool 
    {
        return $this->private;
    }

    /* Le setter de l'attribut private */
    public function setPrivate(string $private) : void
    {
        $this->private = $private;
    }

}